import React from 'react';
import { Typography } from '@material-ui/core'
import { Pie } from 'react-chartjs-2';

import '../css/PieChart.css';


function PieChart({ anualPercentage }) {
    console.log('anualPercentage', anualPercentage);
    const labels = anualPercentage.length > 0 && anualPercentage.map(i => i.label);
    const data = anualPercentage.length > 0 && anualPercentage.map(i => i.value);

    const initialState = {
        // labels: labels,
        // datasets: [
        //     {
        //         label: 'Month',
        //         backgroundColor: 'rgb(35,54,110)',
        //         data: data,
        //     }
        // ]
        pieChartData: {
            labels: ["Africa", "Asia", "Europe", "Latin America", "North America"],
            datasets: [{
              label: "Population (millions)",
              backgroundColor: ["#3e95cd", "#8e5ea2","#3cba9f","#e8c3b9","#c45850"],
              data: [2478,5267,734,784,433]
            }]
          }
    };

    return (
        <>
            <Typography variant="h6">PIE CHART</Typography>
            <Pie data={initialState.pieChartData} className="bar_chart" />
        </>
    )
};

export default PieChart;
